import React, { useEffect, useState } from "react";
import SearchAnimals from "./SearchAnimals";
import AddAnimalForm from "./AddAnimalForm";

function Animals() {
    // Maybe in future change to hold in redux state
    const [animals, setAnimals] = useState([])
    const [addBtn, setAddBtn] = useState(false)
    const [searchValue, setSearchValue] = useState("")

    useEffect(() => {
        fetch("/animals")
        .then((res) => res.json())
        .then((res) => setAnimals(res))
    }, [])

    const animalSearch = animals.filter((animal) => {
        return animal.breed.toLowerCase().includes(searchValue.toLowerCase())
    })

    function tagAnimal(id) {
        console.log(id)
        fetch("/animals", {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(id)
        })
    }

    return (
        <div id="animals_body">

            {/* ADD ANIMAL FORM: */}

            {addBtn === true ? (
                <AddAnimalForm animals={animals} setAnimals={setAnimals} setAddBtn={setAddBtn}/>
            ) : (
                <button id="add_animal_btn" onClick={() => setAddBtn(true)}>+</button>
            )}

            {/* SEARCH ANIMAL'S */}
            <SearchAnimals searchValue={searchValue} onSearchChange={setSearchValue} />

            {/* ANIMAL CARD'S */}

            <div id="animal_cards_container">
                {animalSearch ? animalSearch.map((animal) => {
                    return (
                        <div className="animal_card" key={animal.id}>
                            <div className="animal_img_container">
                                <img src={animal.img} alt={`${animal.name}`} className="animal_img"/>
                            </div>
                            <div className="animal_text_container">
                                <h3>Name: {animal.name}</h3>
                                <p>Species: {animal.species}</p>
                                <p>Breed: {animal.breed}</p>
                                <p>Sex: {animal.sex}</p>
                                <p>Age: {animal.age}</p>
                                <p>Location: {animal.location}</p>
                                <p>Current Owner: {animal.currentOwner}</p>
                            </div>
                            <button onClick={() => tagAnimal(animal.id)} className="tag_icon_btn"><img className="tag_icon" src="https://cdn3.iconfinder.com/data/icons/linecons-free-vector-icons-pack/32/tag-512.png" alt="tag icon"/></button>
                        </div>
                    )
                }) : <h1>Error!</h1>}
            </div>
        </div>
    )
}

export default Animals;
