import React, { useEffect, useState } from "react";
import { useFormik } from "formik";
import * as yup from "yup";

function NewMessage() {
    const [users, setUsers] = useState();

    useEffect(() => {
        fetch("/users")
        .then((res) => res.json())
        .then((res) => setUsers(res))
    }, [])

    const formSchema = yup.object().shape({
        messageTitle: yup.string().required("Please enter a title"),
        messageBody: yup.string().required("Please enter a message body"),
        reciever: yup.string().required("Please select someone to send the message to")
    })

    const formik = useFormik({
        initialValues: {
            messageTitle: "",
            messageBody: "",
            reciever: ""
        },
        validationSchema: formSchema,
        onSubmit: (values) => {
            fetch("/messages", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(values)
            })
        }
    })


    return (
        <div>
            <form onSubmit={formik.handleSubmit} autoComplete="off">
                <input type="text" id="message_title" onChange={formik.handleChange} value={formik.values.messageTitle}/>

                <select id="select_user" onChange={formik.handleChange} values={formik.values.reciever}>
                    <option value="" disabled selected>To</option>
                    {users ? users.map((user) => <option value={user.username}>{user.username}</option>) : null}
                </select>

                <textarea rows="5" cols="33" id="message_body" onChange={formik.handleChange} value={formik.values.messageBody}></textarea>

                <button type="submit">Send</button>
            </form>
        </div>
    )
}

export default NewMessage;
