import { createSlice } from "@reduxjs/toolkit";

// const viewReducer = (state = "login", action) => {
//     switch(action.type) {
//         case "LOGIN":
//             return state = "login"
//         case "SIGNUP":
//             return state = "signup"
//         default:
//             return state;
//     }
// }

const initialState = { value: "signup" }

const viewSlice = createSlice({
    name: "view",
    initialState,
    reducers: {
        viewLogin: (state) => {
            state.view.value = "login"
        },
        viewSignup: (state) => {
            state.view.value = "signup"
        }
    }
})

export const { viewLogin, viewSignup } = viewSlice.actions;
export default viewSlice.reducer
