import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    value: 0
};

export const viewSlice = createSlice({
    name: "view",
    initialState,
    reducers: {
        viewLogin: (state) => {
            state.value += 1;
        },
        viewSignup: (state) => {
            state.value -= 1;
        }
    }
})

export const { viewLogin, viewSignup } = viewSlice.actions;
export default viewSlice.reducer;
